import styled from "styled-components";

export const TableWrap = styled.div`
  table {
    width: 80%;
    margin: 0 auto;
    text-align: center;
    border-spacing: 0;
  }

  TableHead {
    border-bottom: 1px solid #e8e8e8;
    padding: 0;
    font-size: 16px;
    padding: 10px 5px;
    font-weight: bold;
  }

  .common-table-row:hover {
    background-color: #eceaea;
    cursor: pointer;
  }

  .common-table-column {
    padding: 10px 5px;
  }
`;
