import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import "../../src/assets/font/font.css";

const NavWrap = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0px 30px;
  height: 50px;
  background-color: #1e4620;
  font-family: "Jalnan";
`;

const Logo = styled.div`
  color: white;
  text-align: center;
`;

const DropDown = styled.div``;
const DropDownButton = styled.button`
  border: none;
  outline: none;
  background-color: #1e4620;
  position: relative;
  height: 100%;
  color: white;
  text-decoration: none;
  font-size: inherit;
  font-family: "Jalnan";

  :hover {
    color: #dd5528;
  }
`;

const DropDownContents = styled.div`
  display: flex;
  border-radius: 2px;
  position: absolute;
  margin-left: -10px;
  z-index: 1;
  flex-direction: column;
  background-color: #a0b77f;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);

  a {
    padding: 10px 2px;
  }

  ${DropDown}:focus & {
    display: flex;
  }
  ${DropDown}:hover & {
    display: flex;
  }
`;

const Links = styled.div`
  display: flex;
  text-align: center;
  justify-content: space-around;
  height: 100%;
  width: 40%;
  padding: 10px, 5px;

  a {
    align-self: center;
    color: white;
    text-decoration: none;
  }
  a:hover {
    color: #dd5528;
  }
`;

const NavBar = () => {
  const [isLogin, setIsLogin] = useState(false);

  return (
    <NavWrap>
      <Logo>SantaIsSanta</Logo>
      <Links>
        <DropDown>
          <DropDownButton>지역별</DropDownButton>
          <DropDownContents>
            <Link to="/">수도권</Link>
            <Link to="/">강원도</Link>
            <Link to="/">경상도</Link>
            <Link to="/">전라도</Link>
            <Link to="/">충청도</Link>
            <Link to="/">제주도</Link>
          </DropDownContents>
        </DropDown>
        <Link to="/">커뮤니티</Link>
        {isLogin ? <Link to="/">로그인</Link> : <Link to="/">로그아웃</Link>}
        <Link to="/">마이페이지</Link>
      </Links>
    </NavWrap>
  );
};

export default NavBar;
