import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const NavWrap = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0px 30px;
  height: 50px;
  background-color: #1e4620;
`;

const Logo = styled.div`
  color: white;
  text-align: center;
`;

const DropDownButton = styled.button`
  border: none;
  outline: none;
  background-color: #1e4620;
  position: relative;
  height: 50px;
  color: white;
  text-decoration: none;
  font-size: inherit;
`;

const DropDownContents = styled.div`
  display: block;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  border-radius: 2px;
  position: absolute;
  z-index: 1;
  background-color: #a0b77f;
`;

const Links = styled.div`
  display: flex;
  text-align: center;
  justify-content: space-around;
  width: 40%;
  padding: 10px, 5px;

  a {
    display: block;
    text-align: center;
    color: white;
    text-decoration: none;
  }
  a:hover {
    color: #dd5528;
  }
`;

const NavBar = () => {
  const [isLogin, setIsLogin] = useState(false);

  return (
    <NavWrap>
      <Logo>SantaIsSanta</Logo>
      <Links>
        <>
          <DropDownButton>지역별</DropDownButton>
          <DropDownContents>
            <Link to="/">수도권</Link>
            <Link to="#">강원도</Link>
            <Link to="#">경상도</Link>
            <Link to="#">전라도</Link>
            <Link to="#">충청도</Link>
            <Link to="#">제주도</Link>
          </DropDownContents>
        </>
        <Link to="">커뮤니티</Link>
        {isLogin ? <Link to="/">로그인</Link> : <Link to="/">로그아웃</Link>}
        <Link to="/">마이페이지</Link>
      </Links>
    </NavWrap>
  );
};

export default NavBar;
