import React from "react";
import { Link, Routes, Route } from "react-router-dom";
//import "./NavBar.css";
import styled from "styled-components";

const NavBarWrap = styled.div`
  display: flex;
  justify-content: space-between;
  height: 40px;
  background-color: #1e4620;

  link {
    font-size: 16px;
    color: white;
  }
`;

const Dropdown = styled.div`
  float: left;

  .dropdown-content {
    display: none;
    position: absolute;
    background-color: #f9f9f9;
    min-width: 160px;
    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
    z-index: 1;
  }
`;

const NavBar = () => {
  let isLogin = true;

  return (
    <NavBarWrap>
      <Link to="/LandingPage">SantaIsSanta</Link>
      <div>
        <Dropdown>
          <button className="dropbtn">지역별</button>
          <div className="dropdown-content">
            <Link to="#">수도권</Link>
            <Link to="#">강원도</Link>
            <Link to="#">경상도</Link>
            <Link to="#">전라도</Link>
            <Link to="#">충청도</Link>
            <Link to="#">제주도</Link>
          </div>
        </Dropdown>
        <Link to="">커뮤니티</Link>
        {isLogin ? <Link to="/">로그인</Link> : <Link to="/">로그아웃</Link>}
        <Link to="/">마이페이지</Link>
      </div>
    </NavBarWrap>
  );
};

export default NavBar;
