import React from "react";
import { Link, Routes, Route } from "react-router-dom";
//import "./NavBar.css";
import styled from "styled-components";

const NavBarWrap = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 50px;
  background-color: #1e4620;
  font-size: 16px;
`;

const Links = styled.div`
  display: flex;
  justify-content: space-aroud;
`;

const MenuItem = styled.div`
  font-size: 16px;
  color: white;
`;

const Logo = styled.div`
  border: none;
  outline: none;
  font-size: 16px;
  background-color: inherit;
  color: white;
  text-align: center;
  padding: 14px 16px;
`;

const DropDown = styled.button`
  border: none;
  outline: none;
  position: relative;
  font-size: 16px;
  background-color: #1e4620;
  color: white;
`;
const DropDownContents = styled.div`
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  border-radius: 2px;
  padding: 3px;
  margin-top: 7px;
  position: absolute;
  display: none;
  z-index: 1;
  ${DropDown}:active & {
    display: flex;
  }
  ${DropDown}:focus & {
    display: flex;
  }
`;

const NavBar = () => {
  let isLogin = false;

  return (
    <NavBarWrap>
      <Logo to="/">SantaIsSanta</Logo>
      <Links>
        <DropDown>
          지역별
          <DropDownContents>
            <MenuItem component={Link} to={"/first"}>
              수도권
            </MenuItem>
            <MenuItem component={Link} to={"/first"}>
              강원도
            </MenuItem>
            <MenuItem component={Link} to={"/first"}>
              경상도
            </MenuItem>
            <MenuItem component={Link} to={"/first"}>
              전라도
            </MenuItem>
            <MenuItem component={Link} to={"/first"}>
              충청도
            </MenuItem>
            <MenuItem component={Link} to={"/first"}>
              제주도
            </MenuItem>
          </DropDownContents>
        </DropDown>
        <MenuItem component={Link} to={"/first"}>
          커뮤니티
        </MenuItem>
        {isLogin ? (
          <MenuItem component={Link} to={"/first"}>
            로그인
          </MenuItem>
        ) : (
          <MenuItem component={Link} to={"/first"}>
            로그아웃
          </MenuItem>
        )}
        <MenuItem component={Link} to={"/first"}>
          마이페이지
        </MenuItem>
      </Links>
    </NavBarWrap>
  );
};

export default NavBar;
