import React from "react";
import { Link, Routes, Route } from "react-router-dom";
//import "./NavBar.css";
import styled from "styled-components";

const NavBarWrap = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 50px;
  background-color: #1e4620;
  font-size: 16px;
`;

const Logo = styled.div`
  border: none;
  outline: none;
  font-size: 16px;
  background-color: inherit;
  color: white;
  text-align: center;
  padding: 14px 16px;
`;
const DropDown = styled.button`
  border: none;
  outline: none;
  position: relative;
  width: 80px;
`;

const DropDownContents = styled.div`
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  border-radius: 2px;
  padding: 3px;
  margin-top: 7px;
  position: absolute;
  display: none;
  ${DropDown}:active & {
    display: flex;
  }
  ${DropDown}:focus & {
    display: flex;
  }
`;

const NavBar = () => {
  let isLogin = false;

  return (
    <NavBarWrap>
      <Logo>
        <Link to="/">SantaIsSanta</Link>
      </Logo>
      <div>
        <DropDown>
          지역별
          <DropDownContents>
            <Link to="#">수도권</Link>
            <Link to="#">강원도</Link>
            <Link to="#">경상도</Link>
            <Link to="#">전라도</Link>
            <Link to="#">충청도</Link>
            <Link to="#">제주도</Link>
          </DropDownContents>
        </DropDown>
        <Link to="">커뮤니티</Link>
        {isLogin ? <Link to="/">로그인</Link> : <Link to="/">로그아웃</Link>}
        <Link to="/">마이페이지</Link>
      </div>
    </NavBarWrap>
  );
};

export default NavBar;
